import React from "react";

const GameCard = ({
  account,
  onViewDetail,
  onDelete,
  isLoggedIn,
  currentUser,
}) => {
  const canDelete =
    isLoggedIn &&
    (currentUser?.role === "admin" || currentUser?.id === account.ownerId);

  const getImageUrl = () => {
    if (account.images && account.images.length > 0) {
      return account.images[0];
    }
    return `https://picsum.photos/300/200?random=${account.id}`;
  };

  return (
    <div className="group relative bg-gradient-to-br from-white via-blue-50 to-cyan-50 rounded-2xl overflow-hidden shadow-2xl hover:shadow-3xl transition-all duration-500 transform hover:-translate-y-2 border-2 border-cyan-200 backdrop-blur-sm">
      {/* GLOW EFFECT */}
      <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-blue-500 opacity-0 group-hover:opacity-10 blur-xl transition-opacity duration-500"></div>

      {/* ANIMATED BORDER */}
      <div className="absolute inset-0 rounded-2xl bg-gradient-to-r from-cyan-400 via-blue-500 to-purple-500 opacity-0 group-hover:opacity-100 transition-opacity duration-500 -z-10"></div>
      <div className="absolute inset-[2px] rounded-2xl bg-white -z-10"></div>

      <div className="relative z-10">
        {/* IMAGE SECTION */}
        <div className="relative overflow-hidden">
          <img
            src={getImageUrl()}
            alt={account.title || account.category}
            className="w-full h-48 object-cover transform group-hover:scale-110 transition-transform duration-500 border-b-2 border-cyan-200"
          />

          {/* PRICE BADGE - THI·∫æT K·∫æ M·ªöI */}
          <div className="absolute top-3 right-3 bg-gradient-to-r from-amber-400 to-orange-500 text-white font-bold px-4 py-2 rounded-xl shadow-2xl transform group-hover:scale-110 transition-all duration-300 border-2 border-amber-300">
            {account.price.toLocaleString()} VNƒê
          </div>

          {/* CATEGORY BADGE - THI·∫æT K·∫æ M·ªöI */}
          <div className="absolute bottom-3 left-3 bg-gradient-to-r from-blue-500 to-purple-600 text-white text-xs px-3 py-2 rounded-lg backdrop-blur-sm border border-white border-opacity-30 shadow-lg">
            {account.category}
          </div>

          {/* OWNER BADGE - ch·ªâ hi·ªán n·∫øu l√† admin */}
          {currentUser?.role === "admin" && (
            <div className="absolute top-3 left-3 bg-gradient-to-r from-purple-500 to-pink-600 text-white text-xs px-2 py-1 rounded-lg backdrop-blur-sm border border-white border-opacity-30">
              ID: {account.ownerId}
            </div>
          )}
        </div>

        {/* CONTENT SECTION */}
        <div className="p-4">
          <div className="flex justify-between items-start mb-3">
            <h3 className="text-blue-800 font-bold text-lg flex-1 mr-2 group-hover:text-cyan-700 transition-colors duration-300 line-clamp-2">
              {account.title || `Nick ${account.category}`}
            </h3>
          </div>

          {/* BUTTONS - THI·∫æT K·∫æ M·ªöI */}
          <div className="flex justify-between space-x-2">
            <button
              onClick={() => onViewDetail(account)}
              className="flex-1 bg-gradient-to-r from-cyan-500 to-blue-600 text-white px-3 py-3 rounded-xl hover:from-cyan-600 hover:to-blue-700 transition-all duration-300 font-bold shadow-lg transform hover:scale-105 border-2 border-cyan-400 text-sm"
            >
              üíé Xem Chi Ti·∫øt
            </button>

            {canDelete && (
              <button
                onClick={() => onDelete(account.id)}
                className="bg-gradient-to-r from-red-500 to-pink-600 text-white px-3 py-3 rounded-xl hover:from-red-600 hover:to-pink-700 transition-all duration-300 font-bold shadow-lg transform hover:scale-105 border-2 border-red-400 text-sm"
                title="X√≥a nick"
              >
                üóëÔ∏è
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default GameCard;
