import React, { useState, useEffect } from "react";
import CategorySection from "../components/CategorySection";
import GameDetailModal from "../components/GameDetailModal";
import VisitorCounter from "../components/VisitorCounter";

const Home = ({
  accounts,
  categories,
  deleteAccount,
  isLoggedIn,
  currentUser,
  pageViews,
  onIncrementPageViews,
}) => {
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  useEffect(() => {
    onIncrementPageViews();
  }, []);

  const handleViewDetail = (account) => {
    setSelectedAccount(account);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedAccount(null);
  };

  return (
    <div className="min-h-screen">
      <div className="container mx-auto px-4 py-8">
        {/* HERO SECTION - BỐ CỤC MỚI */}
        <div className="mb-12">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
            {/* TÊN WEBSITE */}
            <div className="lg:col-span-2">
              <div className="bg-gradient-to-r from-blue-600 to-blue-800 rounded-2xl p-8 shadow-2xl border border-blue-300">
                <h1 className="text-4xl md:text-5xl font-bold text-white text-center">
                  CHỢ ĐEN PUBG PC
                </h1>
                <div className="mt-4 bg-white bg-opacity-20 rounded-lg p-4">
                  <p className="text-white text-lg text-center font-medium">
                    MUA BÁN TRAO ĐỔI TÀI KHOẢN PUBG PC
                  </p>
                </div>
              </div>
            </div>

            {/* THÔNG TIN LIÊN HỆ */}
            <div className="bg-gradient-to-br from-cyan-500 to-blue-600 rounded-2xl p-6 shadow-2xl border border-cyan-300">
              <div className="text-center">
                <div className="bg-white bg-opacity-20 rounded-lg p-4 mb-4">
                  <p className="text-white text-sm font-medium mb-2">LIÊN HỆ</p>
                  <p className="text-white text-2xl font-bold">0922.010011</p>
                </div>
                <div className="bg-white rounded-lg p-3">
                  <p className="text-blue-600 text-sm font-medium">
                    Hỗ trợ 24/7
                  </p>
                </div>
              </div>
            </div>
          </div>

          {/* VISITOR COUNTER - VỊ TRÍ MỚI */}
          <div className="max-w-md mx-auto">
            <VisitorCounter count={pageViews} />
          </div>
        </div>

        {/* CATEGORIES */}
        {categories.map((category) => (
          <CategorySection
            key={category.id}
            category={category.name}
            accounts={accounts}
            onViewDetail={handleViewDetail}
            onDelete={deleteAccount}
            isLoggedIn={isLoggedIn}
            currentUser={currentUser}
          />
        ))}

        {accounts.length === 0 && (
          <div className="text-center py-16">
            <div className="bg-white rounded-2xl p-8 max-w-md mx-auto shadow-lg border border-blue-200">
              <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <div className="w-8 h-8 bg-blue-500 rounded-full animate-pulse"></div>
              </div>
              <h3 className="text-xl font-bold text-blue-800 mb-2">
                ĐANG CẬP NHẬT NICK
              </h3>
              <p className="text-blue-600">
                Chúng tôi đang bổ sung thêm nick game chất lượng!
              </p>
            </div>
          </div>
        )}

        <GameDetailModal
          account={selectedAccount}
          isOpen={isModalOpen}
          onClose={handleCloseModal}
        />
      </div>
    </div>
  );
};

export default Home;
