import React, { useState, useEffect } from "react";
import CategorySection from "../components/CategorySection";
import GameDetailModal from "../components/GameDetailModal";
import VisitorCounter from "../components/VisitorCounter";

const Home = ({
  accounts,
  categories,
  deleteAccount,
  isLoggedIn,
  currentUser,
  pageViews,
  onIncrementPageViews,
}) => {
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Tăng page views khi component mount
  useEffect(() => {
    onIncrementPageViews();
  }, []);

  const handleViewDetail = (account) => {
    setSelectedAccount(account);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedAccount(null);
  };

  return (
    <div className="min-h-screen bg-white">
      <div className="container mx-auto px-4 py-8">
        {/* HERO SECTION */}
        <div className="text-center mb-12">
          <div className="mb-8">
            <div className="bg-blue-600 rounded-lg p-6 inline-block">
              <h1 className="text-4xl md:text-5xl font-bold text-white">
                CHỢ ĐEN PUBG PC
              </h1>
            </div>
          </div>

          <div className="bg-blue-50 border border-blue-200 rounded-lg p-6 max-w-2xl mx-auto">
            <p className="text-xl md:text-2xl font-bold text-blue-800 mb-4">
              MUA BÁN TRAO ĐỔI TÀI KHOẢN PUBG PC
            </p>
            <div className="bg-blue-600 rounded-lg p-4 mt-4">
              <p className="text-white text-lg font-bold">
                LIÊN HỆ: <span className="text-white text-xl">0922.010011</span>
              </p>
            </div>
          </div>

          {/* VISITOR COUNTER */}
          <VisitorCounter count={pageViews} />
        </div>

        {/* CATEGORIES */}
        {categories.map((category) => (
          <CategorySection
            key={category.id}
            category={category.name}
            accounts={accounts}
            onViewDetail={handleViewDetail}
            onDelete={deleteAccount}
            isLoggedIn={isLoggedIn}
            currentUser={currentUser}
          />
        ))}

        {accounts.length === 0 && (
          <div className="text-center py-16">
            <div className="bg-blue-50 border border-blue-200 rounded-lg p-8 max-w-md mx-auto">
              <h3 className="text-2xl font-bold text-blue-800 mb-4">
                ĐANG CẬP NHẬT NICK
              </h3>
              <p className="text-blue-600 text-lg">
                Chúng tôi đang bổ sung thêm nick game chất lượng!
              </p>
            </div>
          </div>
        )}

        <GameDetailModal
          account={selectedAccount}
          isOpen={isModalOpen}
          onClose={handleCloseModal}
        />
      </div>
    </div>
  );
};

export default Home;
