import React, { useState } from "react";

const GameDetailModal = ({ account, isOpen, onClose }) => {
  const [selectedImageIndex, setSelectedImageIndex] = useState(null);
  const [showImageViewer, setShowImageViewer] = useState(false);

  if (!isOpen || !account) return null;

  const getImageUrl = (imageUrl) => {
    return imageUrl;
  };

  const openImageViewer = (index) => {
    setSelectedImageIndex(index);
    setShowImageViewer(true);
  };

  const closeImageViewer = () => {
    setShowImageViewer(false);
    setSelectedImageIndex(null);
  };

  const goToNextImage = () => {
    setSelectedImageIndex((prev) =>
      prev < account.images.length - 1 ? prev + 1 : prev
    );
  };

  const goToPrevImage = () => {
    setSelectedImageIndex((prev) => (prev > 0 ? prev - 1 : prev));
  };

  return (
    <>
      {/* MAIN MODAL */}
      <div className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center z-50 p-4">
        <div className="bg-gradient-to-br from-white via-blue-50 to-cyan-50 rounded-3xl shadow-2xl max-w-4xl w-full max-h-[90vh] overflow-y-auto border-2 border-cyan-300 relative backdrop-blur-sm">
          {/* CLOSE BUTTON */}
          <button
            onClick={onClose}
            className="absolute top-4 right-4 bg-gradient-to-r from-red-500 to-pink-600 text-white text-lg w-10 h-10 rounded-full hover:from-red-600 hover:to-pink-700 transition-all duration-300 z-20 shadow-lg border-2 border-white flex items-center justify-center transform hover:scale-110"
          >
            ‚úï
          </button>

          <div className="relative z-10 p-6">
            {/* HEADER */}
            <div className="text-center mb-6">
              <h2 className="text-3xl font-bold bg-gradient-to-r from-blue-600 via-purple-600 to-cyan-600 bg-clip-text text-transparent">
                {account.title || `Nick ${account.category}`}
              </h2>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-6">
              {/* IMAGES SECTION */}
              <div>
                <h3 className="text-lg font-bold text-blue-800 mb-3">
                  ·∫¢nh minh h·ªça
                </h3>
                <div className="grid grid-cols-2 gap-3">
                  {account.images && account.images.length > 0 ? (
                    account.images.map((imageUrl, index) => (
                      <div
                        key={index}
                        className="relative group cursor-pointer"
                        onClick={() => openImageViewer(index)}
                      >
                        <img
                          src={getImageUrl(imageUrl)}
                          alt={`${account.title} ${index + 1}`}
                          className="w-full h-32 object-cover rounded-xl border-2 border-cyan-300 transform group-hover:scale-105 transition-all duration-300 shadow-lg"
                        />
                        <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-30 rounded-xl transition-all duration-300 flex items-center justify-center">
                          <span className="text-white text-sm opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                            üëÅÔ∏è
                          </span>
                        </div>
                      </div>
                    ))
                  ) : (
                    <img
                      src={`https://picsum.photos/300/200?random=${account.id}`}
                      alt={account.title}
                      className="w-full h-32 object-cover rounded-xl border-2 border-cyan-300"
                    />
                  )}
                </div>
              </div>

              {/* DETAILS SECTION */}
              <div className="space-y-4">
                <div className="bg-gradient-to-r from-blue-100 to-cyan-100 rounded-xl p-4 border-2 border-blue-200">
                  <h3 className="text-lg font-bold text-blue-800 mb-2">
                    Th√¥ng tin chi ti·∫øt
                  </h3>
                  <p className="text-blue-700 text-sm leading-relaxed">
                    {account.details}
                  </p>
                </div>

                <div className="bg-gradient-to-r from-amber-400 to-orange-500 rounded-xl p-4 text-center border-2 border-amber-300 shadow-lg">
                  <h3 className="text-lg font-bold text-white mb-2">
                    Gi√°: {account.price.toLocaleString()} VNƒê
                  </h3>
                  <p className="text-white">Lo·∫°i: {account.category}</p>
                </div>
              </div>
            </div>

            {/* CONTACT SECTION */}
            <div className="bg-gradient-to-r from-cyan-500 to-blue-600 rounded-xl p-6 text-center border-2 border-cyan-400 shadow-lg">
              <h3 className="text-xl font-bold text-white mb-4">
                LI√äN H·ªÜ MUA NICK
              </h3>
              <a
                href={account.facebookLink || "https://facebook.com"}
                target="_blank"
                rel="noopener noreferrer"
                className="inline-block bg-gradient-to-r from-green-400 to-emerald-500 text-white font-bold text-lg px-8 py-4 rounded-xl hover:from-green-500 hover:to-emerald-600 transition-all duration-300 shadow-2xl transform hover:scale-105 border-2 border-emerald-400"
              >
                üì± NH·∫§N V√ÄO ƒê√ÇY ƒê·ªÇ LI√äN H·ªÜ FACEBOOK
              </a>
              <p className="text-cyan-100 mt-3 text-sm">
                Click v√†o n√∫t tr√™n ƒë·ªÉ ƒë∆∞·ª£c chuy·ªÉn ƒë·∫øn Facebook ng∆∞·ªùi b√°n
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* IMAGE VIEWER OVERLAY */}
      {showImageViewer && (
        <div className="fixed inset-0 bg-black bg-opacity-95 z-[9999] flex items-center justify-center p-4">
          {/* CLOSE BUTTON */}
          <button
            onClick={closeImageViewer}
            className="absolute top-4 right-4 bg-gradient-to-r from-red-500 to-pink-600 text-white text-xl w-12 h-12 rounded-full hover:from-red-600 hover:to-pink-700 transition-all duration-300 z-50 border-2 border-white flex items-center justify-center transform hover:scale-110"
          >
            ‚úï
          </button>

          {/* PREVIOUS BUTTON */}
          {selectedImageIndex > 0 && (
            <button
              onClick={goToPrevImage}
              className="absolute left-4 bg-gradient-to-r from-cyan-500 to-blue-600 text-white text-2xl w-12 h-12 rounded-full hover:from-cyan-600 hover:to-blue-700 transition-all duration-300 z-50 border-2 border-white flex items-center justify-center transform hover:scale-110"
            >
              ‚Äπ
            </button>
          )}

          {/* NEXT BUTTON */}
          {selectedImageIndex < account.images.length - 1 && (
            <button
              onClick={goToNextImage}
              className="absolute right-4 bg-gradient-to-r from-cyan-500 to-blue-600 text-white text-2xl w-12 h-12 rounded-full hover:from-cyan-600 hover:to-blue-700 transition-all duration-300 z-50 border-2 border-white flex items-center justify-center transform hover:scale-110"
            >
              ‚Ä∫
            </button>
          )}

          {/* IMAGE CONTAINER */}
          <div
            className="max-w-4xl max-h-full flex items-center justify-center p-4 z-40"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="bg-gradient-to-r from-cyan-500 to-blue-600 p-2 rounded-xl">
              <img
                src={getImageUrl(account.images[selectedImageIndex])}
                className="max-w-full max-h-[70vh] object-contain rounded-xl border-2 border-cyan-300 shadow-2xl"
                alt={`${account.title} ${selectedImageIndex + 1}`}
                onClick={(e) => e.stopPropagation()}
              />
            </div>
          </div>

          {/* IMAGE COUNTER */}
          <div className="absolute bottom-4 bg-black bg-opacity-70 text-white text-sm px-4 py-2 rounded-lg border border-cyan-400 z-50">
            {selectedImageIndex + 1} / {account.images.length}
          </div>

          {/* BACKDROP CLICK AREA */}
          <div
            className="absolute inset-0 z-30"
            onClick={closeImageViewer}
          ></div>
        </div>
      )}
    </>
  );
};

export default GameDetailModal;
