import React, { useState, useEffect } from "react";

const VisitorCounter = ({ count }) => {
  const [displayCount, setDisplayCount] = useState(0);

  useEffect(() => {
    if (count > 0) {
      const duration = 2000;
      const steps = 60;
      const stepTime = duration / steps;
      const stepValue = count / steps;

      let currentStep = 0;
      const timer = setInterval(() => {
        currentStep++;
        const currentValue = Math.min(
          Math.floor(stepValue * currentStep),
          count
        );
        setDisplayCount(currentValue);

        if (currentStep >= steps) {
          clearInterval(timer);
          setDisplayCount(count);
        }
      }, stepTime);

      return () => clearInterval(timer);
    }
  }, [count]);

  return (
    <div className="mt-8 bg-blue-50 border border-blue-200 rounded-lg p-6 max-w-md mx-auto">
      <div className="bg-white rounded-lg p-4 border border-blue-300">
        <h3 className="text-blue-800 font-medium mb-2 text-center">
          Lượt Truy Cập
        </h3>
        <div className="text-center">
          <span className="text-3xl font-bold text-blue-600">
            {displayCount.toLocaleString()}
          </span>
        </div>
        <p className="text-blue-600 text-sm text-center mt-2">
          Người đã ghé thăm website
        </p>
      </div>
    </div>
  );
};

export default VisitorCounter;
