import React, { useState, useEffect } from "react";

const VisitorCounter = ({ count }) => {
  const [displayCount, setDisplayCount] = useState(0);

  useEffect(() => {
    if (count > 0) {
      const duration = 2000;
      const steps = 60;
      const stepTime = duration / steps;
      const stepValue = count / steps;

      let currentStep = 0;
      const timer = setInterval(() => {
        currentStep++;
        const currentValue = Math.min(
          Math.floor(stepValue * currentStep),
          count
        );
        setDisplayCount(currentValue);

        if (currentStep >= steps) {
          clearInterval(timer);
          setDisplayCount(count);
        }
      }, stepTime);

      return () => clearInterval(timer);
    }
  }, [count]);

  return (
    <div className="bg-gradient-to-r from-cyan-500 via-blue-600 to-purple-600 rounded-2xl p-1 shadow-2xl border-2 border-white border-opacity-30 backdrop-blur-sm">
      <div className="bg-gradient-to-br from-white via-blue-50 to-cyan-50 rounded-xl p-6 border-2 border-cyan-200">
        <h3 className="text-blue-800 font-bold text-lg mb-3 text-center">
          üë• L∆∞·ª£t Truy C·∫≠p
        </h3>
        <div className="text-center mb-3">
          <span className="text-5xl font-bold bg-gradient-to-r from-cyan-600 via-blue-700 to-purple-700 bg-clip-text text-transparent animate-pulse">
            {displayCount.toLocaleString()}
          </span>
        </div>
        <p className="text-blue-600 text-sm text-center font-medium">
          Ng∆∞·ªùi ƒë√£ gh√© thƒÉm website
        </p>

        {/* ANIMATED DOTS */}
        <div className="flex justify-center space-x-2 mt-3">
          <div className="w-2 h-2 bg-cyan-400 rounded-full animate-bounce"></div>
          <div
            className="w-2 h-2 bg-blue-500 rounded-full animate-bounce"
            style={{ animationDelay: "0.1s" }}
          ></div>
          <div
            className="w-2 h-2 bg-purple-500 rounded-full animate-bounce"
            style={{ animationDelay: "0.2s" }}
          ></div>
        </div>
      </div>
    </div>
  );
};

export default VisitorCounter;
