import React, { useState } from "react";

const LoginModal = ({ isOpen, onClose, onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [showPromotion, setShowPromotion] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    try {
      const result = await onLogin(username, password);

      if (result && result.success) {
        setError("");
        setUsername("");
        setPassword("");
        onClose();
      } else {
        setError(result?.error || "ƒêƒÉng nh·∫≠p th·∫•t b·∫°i");
      }
    } catch (error) {
      setError("L·ªói k·∫øt n·ªëi ƒë·∫øn server");
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    if (!loading) {
      // Khi click d·∫•u X, hi·ªÉn th·ªã trang qu·∫£ng b√° thay v√¨ ƒë√≥ng lu√¥n
      setShowPromotion(true);
    }
  };

  const handleClosePromotion = () => {
    setShowPromotion(false);
    onClose(); // ƒê√≥ng modal sau khi ƒë√≥ng trang qu·∫£ng b√°
  };

  const handleBackToLogin = () => {
    setShowPromotion(false);
  };

  if (!isOpen) return null;

  // N·∫æU ƒêANG HI·ªÜN TRANG QU·∫¢NG B√Å
  if (showPromotion) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-90 flex items-center justify-center z-50 p-4">
        <div className="bg-gradient-to-br from-white via-blue-50 to-purple-50 rounded-3xl shadow-2xl w-full max-w-2xl mx-auto border-2 border-cyan-300 relative backdrop-blur-sm">
          <div className="absolute inset-0 bg-gradient-to-r from-cyan-400 to-blue-500 rounded-3xl opacity-10 blur-xl"></div>

          <div className="relative z-10">
            {/* HEADER */}
            <div className="flex justify-between items-center p-6 border-b-2 border-cyan-200">
              <h2 className="text-2xl font-bold bg-gradient-to-r from-blue-600 via-purple-600 to-cyan-600 bg-clip-text text-transparent">
                D·ªãch V·ª• Thi·∫øt K·∫ø Website
              </h2>
              <button
                onClick={handleClosePromotion}
                className="bg-gradient-to-r from-red-500 to-pink-600 text-white text-xl w-10 h-10 rounded-full hover:from-red-600 hover:to-pink-700 transition-all duration-300 border-2 border-white flex items-center justify-center transform hover:scale-110"
              >
                ‚úï
              </button>
            </div>

            {/* CONTENT */}
            <div className="p-6">
              <div className="text-center mb-6">
                <div className="w-20 h-20 bg-gradient-to-r from-cyan-500 to-blue-600 rounded-full flex items-center justify-center mx-auto mb-4">
                  <span className="text-white text-2xl font-bold">üíé</span>
