import React, { useState, useEffect } from "react";

const VisitorCounter = ({ count }) => {
  const [displayCount, setDisplayCount] = useState(0);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    if (count > 0) {
      const duration = 2000; // 2 giây
      const startTime = Date.now();

      const updateCounter = () => {
        const elapsed = Date.now() - startTime;
        const progress = Math.min(elapsed / duration, 1);

        setProgress(progress);
        setDisplayCount(Math.floor(count * progress));

        if (progress < 1) {
          requestAnimationFrame(updateCounter);
        } else {
          setDisplayCount(count);
          setProgress(1);
        }
      };

      requestAnimationFrame(updateCounter);
    }
  }, [count]);

  return (
    <div className="bg-gradient-to-r from-blue-600 to-purple-700 rounded-2xl p-1 shadow-2xl border-2 border-cyan-400">
      <div className="bg-gradient-to-br from-white via-blue-50 to-cyan-50 rounded-xl p-5 border-2 border-cyan-300">
        <h3 className="text-blue-800 font-bold text-base mb-3 text-center">
          Lượt Truy Cập
        </h3>

        {/* PROGRESS BAR */}
        <div className="w-full bg-blue-200 rounded-full h-2 mb-3 overflow-hidden">
          <div
            className="bg-gradient-to-r from-cyan-500 to-blue-600 h-2 rounded-full transition-all duration-100 ease-out"
            style={{ width: `${progress * 100}%` }}
          ></div>
        </div>

        <div className="text-center mb-2">
          <span className="text-3xl font-bold bg-gradient-to-r from-blue-700 to-purple-700 bg-clip-text text-transparent">
            {displayCount.toLocaleString()}
          </span>
        </div>

        <p className="text-blue-600 text-xs text-center font-medium">
          Người đã ghé thăm website
        </p>

        {/* ANIMATED DOTS */}
        <div className="flex justify-center space-x-1 mt-2">
          <div
            className={`w-1 h-1 bg-cyan-500 rounded-full transition-all duration-300 ${
              progress > 0.3 ? "opacity-100" : "opacity-30"
            }`}
          ></div>
          <div
            className={`w-1 h-1 bg-blue-500 rounded-full transition-all duration-300 ${
              progress > 0.6 ? "opacity-100" : "opacity-30"
            }`}
          ></div>
          <div
            className={`w-1 h-1 bg-purple-500 rounded-full transition-all duration-300 ${
              progress > 0.9 ? "opacity-100" : "opacity-30"
            }`}
          ></div>
        </div>
      </div>
    </div>
  );
};

export default VisitorCounter;
