import React from "react";

const GameCard = ({
  account,
  onViewDetail,
  onDelete,
  isLoggedIn,
  currentUser,
}) => {
  const canDelete =
    isLoggedIn &&
    (currentUser?.role === "admin" || currentUser?.id === account.ownerId);

  const getImageUrl = () => {
    if (account.images && account.images.length > 0) {
      return account.images[0];
    }
    return `https://picsum.photos/300/200?random=${account.id}`;
  };

  return (
    <div className="bg-white rounded-lg shadow-sm border border-blue-200 overflow-hidden hover:shadow-md transition-shadow">
      <div className="relative">
        <img
          src={getImageUrl()}
          alt={account.title || account.category}
          className="w-full h-48 object-cover"
        />

        <div className="absolute top-3 right-3 bg-blue-600 text-white font-medium px-3 py-1 rounded shadow-sm">
          {account.price.toLocaleString()} VNĐ
        </div>

        <div className="absolute bottom-3 left-3 bg-black bg-opacity-70 text-white text-xs px-2 py-1 rounded">
          {account.category}
        </div>
      </div>

      <div className="p-4">
        <div className="flex justify-between items-start mb-3">
          <h3 className="text-blue-800 font-medium text-lg flex-1 mr-2">
            {account.title || `Nick ${account.category}`}
          </h3>
        </div>

        <div className="flex justify-between space-x-2">
          <button
            onClick={() => onViewDetail(account)}
            className="flex-1 bg-blue-600 text-white px-3 py-2 rounded hover:bg-blue-700 transition-colors font-medium border border-blue-600"
          >
            Xem Chi Tiết
          </button>

          {canDelete && (
            <button
              onClick={() => onDelete(account.id)}
              className="bg-red-600 text-white px-3 py-2 rounded hover:bg-red-700 transition-colors font-medium border border-red-600"
            >
              Xóa
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default GameCard;
