import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const API_URL = "http://localhost:8000/api";

const DatabaseManagement = ({ currentUser }) => {
  const navigate = useNavigate();
  const [tablesData, setTablesData] = useState({});
  const [loading, setLoading] = useState(true);
  const [selectedTable, setSelectedTable] = useState("game_nicks");
  const [selectedRows, setSelectedRows] = useState([]);
  const [selectAll, setSelectAll] = useState(false);
  const [isAuthorized, setIsAuthorized] = useState(false);

  useEffect(() => {
    if (currentUser?.id === 1) {
      setIsAuthorized(true);
      fetchTablesData();
    } else {
      setIsAuthorized(false);
      setLoading(false);
    }
  }, [currentUser]);

  const fetchTablesData = async () => {
    try {
      setLoading(true);
      const response = await fetch(`${API_URL}/database/tables`);
      const data = await response.json();
      setTablesData(data);
    } catch (error) {
      console.error("Error fetching tables data:", error);
      alert("L·ªói khi t·∫£i d·ªØ li·ªáu database");
    } finally {
      setLoading(false);
    }
  };

  const handleRowSelect = (id) => {
    setSelectedRows((prev) =>
      prev.includes(id) ? prev.filter((rowId) => rowId !== id) : [...prev, id]
    );
  };

  const handleSelectAll = () => {
    if (selectAll) {
      setSelectedRows([]);
    } else {
      const allIds = tablesData[selectedTable]?.map((row) => row.id) || [];
      setSelectedRows(allIds);
    }
    setSelectAll(!selectAll);
  };

  const handleBulkDelete = async () => {
    if (selectedRows.length === 0) {
      alert("Vui l√≤ng ch·ªçn √≠t nh·∫•t m·ªôt b·∫£n ghi ƒë·ªÉ x√≥a");
      return;
    }

    if (
      !window.confirm(
        `B·∫°n c√≥ ch·∫Øc mu·ªën x√≥a ${selectedRows.length} b·∫£n ghi t·ª´ b·∫£ng ${selectedTable}?`
      )
    ) {
      return;
    }

    try {
      const response = await fetch(`${API_URL}/database/bulk-delete`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          table_name: selectedTable,
          ids: selectedRows,
        }),
      });

      const result = await response.json();

      if (result.success) {
        alert(result.message);
        setSelectedRows([]);
        setSelectAll(false);
        await fetchTablesData();
      } else {
        alert(result.detail || "L·ªói khi x√≥a d·ªØ li·ªáu");
      }
    } catch (error) {
      console.error("Error deleting records:", error);
      alert("L·ªói khi x√≥a d·ªØ li·ªáu");
    }
  };

  const handleClearTable = async () => {
    if (
      !window.confirm(
        `B·∫°n c√≥ CH·∫ÆC CH·∫ÆN mu·ªën x√≥a TO√ÄN B·ªò d·ªØ li·ªáu trong b·∫£ng ${selectedTable}?`
      )
    ) {
      return;
    }

    try {
      const response = await fetch(
        `${API_URL}/database/clear-table/${selectedTable}`,
        {
          method: "DELETE",
        }
      );

      const result = await response.json();

      if (result.success) {
        alert(result.message);
        await fetchTablesData();
      } else {
        alert(result.detail || "L·ªói khi x√≥a b·∫£ng");
      }
    } catch (error) {
      console.error("Error clearing table:", error);
      alert("L·ªói khi x√≥a b·∫£ng");
    }
  };

  const renderTableData = () => {
    const data = tablesData[selectedTable] || [];

    if (data.length === 0) {
      return (
        <div className="text-center py-8 text-blue-600">
          <p>Kh√¥ng c√≥ d·ªØ li·ªáu trong b·∫£ng n√†y</p>
        </div>
      );
    }

    const columns = Object.keys(data[0] || {});

    return (
      <div className="overflow-x-auto rounded-xl border-2 border-blue-200">
        <table className="w-full bg-white rounded-lg overflow-hidden">
          <thead>
            <tr className="bg-gradient-to-r from-cyan-500 to-blue-600">
              <th className="p-3 text-left">
                <input
                  type="checkbox"
                  checked={selectAll}
                  onChange={handleSelectAll}
                  className="w-4 h-4"
                />
              </th>
              {columns.map((col) => (
                <th
                  key={col}
                  className="p-3 text-left text-white font-bold text-sm"
                >
                  {col.toUpperCase()}
                </th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((row, index) => (
              <tr
                key={row.id}
                className={`border-b border-blue-200 ${
                  index % 2 === 0 ? "bg-blue-50" : "bg-white"
                } hover:bg-cyan-50 transition-colors`}
              >
                <td className="p-3">
                  <input
                    type="checkbox"
                    checked={selectedRows.includes(row.id)}
                    onChange={() => handleRowSelect(row.id)}
                    className="w-4 h-4"
                  />
                </td>
                {columns.map((col) => (
                  <td
                    key={col}
                    className="p-3 text-blue-800 text-sm max-w-xs truncate"
                  >
                    {Array.isArray(row[col])
                      ? `[${row[col].length} items]`
                      : String(row[col] || "")}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  if (!isAuthorized) {
    return (
      <div className="max-w-6xl mx-auto p-4">
        <div className="bg-gradient-to-br from-red-500 to-pink-600 rounded-2xl p-6 border-2 border-red-400 text-center shadow-2xl">
          <h2 className="text-xl font-bold text-white mb-4">
            ‚ö†Ô∏è Kh√¥ng c√≥ quy·ªÅn truy c·∫≠p
          </h2>
          <p className="text-red-100">
            Ch·ªâ admin g·ªëc m·ªõi ƒë∆∞·ª£c truy c·∫≠p trang n√†y.
          </p>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="max-w-6xl mx-auto p-4">
        <div className="text-center text-blue-600">ƒêang t·∫£i d·ªØ li·ªáu...</div>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto p-4">
      <div className="bg-gradient-to-br from-white via-blue-50 to-cyan-50 rounded-3xl shadow-2xl p-6 border-2 border-cyan-200 backdrop-blur-sm">
        {/* HEADER */}
