import React, { useState, useEffect } from "react";
import LoginModal from "./LoginModal";

const API_URL = "http://localhost:8000/api";

const AdminPanel = ({
  addAccount,
  categories,
  addCategory,
  removeCategory,
  reorderCategory,
  isLoggedIn,
  currentUser,
}) => {
  const [showLogin, setShowLogin] = useState(false);
  const [showAdminContent, setShowAdminContent] = useState(false);
  const [showAddCategory, setShowAddCategory] = useState(false);
  const [newCategoryName, setNewCategoryName] = useState("");
  const [uploading, setUploading] = useState(false);

  const [formData, setFormData] = useState({
    title: "",
    category: "",
    price: "",
    details: "",
    facebookLink: "https://www.facebook.com/letuan089",
    images: [],
  });

  useEffect(() => {
    if (isLoggedIn) {
      setShowAdminContent(true);
      setShowLogin(false);
    } else {
      setShowAdminContent(false);
      setShowLogin(true);
    }
  }, [isLoggedIn]);

  const handleLoginSuccess = () => {
    setShowAdminContent(true);
    setShowLogin(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleImageUpload = async (e) => {
    const files = Array.from(e.target.files);
    if (files.length === 0) return;

    setUploading(true);

    for (const file of files) {
      const formData = new FormData();
      formData.append("file", file);

      try {
        const response = await fetch(`${API_URL}/upload/`, {
          method: "POST",
          body: formData,
        });

        if (response.ok) {
          const result = await response.json();
          if (result.success) {
            setFormData((prev) => ({
              ...prev,
              images: [...prev.images, result.url],
            }));
          }
        }
      } catch (error) {
        console.error("Upload error:", error);
        alert(`Lỗi upload ảnh: ${error.message}`);
      }
    }

    setUploading(false);
  };

  const removeImage = (index) => {
    const updatedImages = [...formData.images];
    updatedImages.splice(index, 1);
    setFormData({
      ...formData,
      images: updatedImages,
    });
  };

  const handleAddCategory = async () => {
    if (
      newCategoryName.trim() &&
      !categories.find((cat) => cat.name === newCategoryName.trim())
    ) {
      await addCategory(newCategoryName.trim());
      setFormData({
        ...formData,
        category: newCategoryName.trim(),
      });
      setNewCategoryName("");
      setShowAddCategory(false);
    }
  };

  const handleRemoveCategory = async (categoryToRemove) => {
    if (
      window.confirm(
        `Bạn có chắc chắn muốn xóa loại nick "${categoryToRemove}" không?`
      )
    ) {
      await removeCategory(categoryToRemove);
      if (formData.category === categoryToRemove) {
        setFormData({ ...formData, category: "" });
      }
    }
  };

  const handleReorderCategory = async (categoryId, direction) => {
    await reorderCategory(categoryId, direction);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newAccount = {
      title: formData.title,
      category: formData.category,
      price: parseInt(formData.price) || 0,
      details: formData.details,
      facebookLink: formData.facebookLink,
      images: formData.images,
    };

    const success = await addAccount(newAccount);

    if (success) {
      setFormData({
        title: "",
        category: "",
        price: "",
        details: "",
        facebookLink: "https://www.facebook.com/letuan089",
        images: [],
      });
      alert("Đã thêm nick thành công!");
    } else {
      alert("Không thể thêm nick! Vui lòng thử lại.");
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-4">
      <LoginModal
        isOpen={showLogin}
        onClose={() => setShowLogin(false)}
        onLogin={handleLoginSuccess}
      />

      {showAdminContent && (
        <div className="bg-white rounded-lg shadow-sm p-6 border border-blue-200">
          <div className="text-center mb-6">
            <h1 className="text-xl font-bold text-blue-800">
              Quản Lý Nick Game
            </h1>
            <p className="text-blue-600 text-sm mt-2">
              {currentUser?.role === "admin" ? "Quản trị viên" : "Thành viên"} -{" "}
              {currentUser?.username}
            </p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 gap-4">
              <div>
                <label className="block text-blue-700 font-medium mb-2">
                  Tiêu đề nick
                </label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 rounded border border-blue-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Ví dụ: Nick VIP PUBG full skin..."
                />
              </div>

              <div>
                <label className="block text-blue-700 font-medium mb-2">
                  Loại nick *
                </label>
                <div className="flex space-x-2">
                  <select
                    name="category"
                    value={formData.category}
                    onChange={handleInputChange}
                    className="flex-1 px-3 py-2 rounded border border-blue-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  >
                    <option value="">Chọn loại nick</option>
                    {categories.map((cat) => (
                      <option key={cat.id} value={cat.name}>
                        {cat.name}
                      </option>
                    ))}
                  </select>
                  <button
                    type="button"
                    onClick={() => setShowAddCategory(!showAddCategory)}
                    className="bg-blue-600 text-white px-3 py-2 rounded hover:bg-blue-700 transition-colors border border-blue-600"
                  >
                    {showAddCategory ? "Hủy" : "Thêm"}
                  </button>
                </div>

                {showAddCategory && (
                  <div className="mt-2 flex space-x-2">
                    <input
                      type="text"
                      value={newCategoryName}
                      onChange={(e) => setNewCategoryName(e.target.value)}
                      className="flex-1 px-3 py-2 rounded border border-blue-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
                      placeholder="Nhập tên loại nick mới"
                    />
                    <button
                      type="button"
                      onClick={handleAddCategory}
                      className="bg-green-600 text-white px-3 py-2 rounded hover:bg-green-700 transition-colors border border-green-600"
                    >
                      Lưu
                    </button>
                  </div>
                )}
              </div>

              <div>
                <label className="block text-blue-700 font-medium mb-2">
                  Giá (VNĐ) *
                </label>
                <input
                  type="number"
                  name="price"
                  value={formData.price}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 rounded border border-blue-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Nhập giá nick..."
                  required
                />
              </div>

              <div>
                <label className="block text-blue-700 font-medium mb-2">
                  Thông tin chi tiết *
                </label>
                <textarea
                  name="details"
                  value={formData.details}
                  onChange={handleInputChange}
                  rows="3"
                  className="w-full px-3 py-2 rounded border border-blue-300 focus:outline-none focus:ring-2 focus:ring-blue-500 resize-none"
                  placeholder="Mô tả chi tiết về nick..."
                  required
                ></textarea>
              </div>

              <div>
                <label className="block text-blue-700 font-medium mb-2">
                  Link Facebook *
                </label>
                <input
                  type="url"
                  name="facebookLink"
                  value={formData.facebookLink}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 rounded border border-blue-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="https://facebook.com/..."
                  required
                />
              </div>

              <div>
                <div className="flex justify-between items-center mb-2">
                  <label className="block text-blue-700 font-medium">
                    Ảnh minh họa {uploading && "(Đang upload...)"}
                  </label>
                  <label className="bg-blue-600 text-white px-4 py-2 rounded cursor-pointer hover:bg-blue-700 transition-colors border border-blue-600">
                    Chọn ảnh
                    <input
                      type="file"
                      multiple
                      accept="image/jpeg, image/png, image/webp"
                      onChange={handleImageUpload}
                      className="hidden"
                      disabled={uploading}
                    />
                  </label>
                </div>

                <div className="grid grid-cols-2 gap-3 mb-3">
                  {formData.images.map((imageUrl, index) => (
                    <div key={index} className="relative">
                      <img
                        src={imageUrl}
                        alt={`Preview ${index + 1}`}
                        className="w-full h-20 object-cover rounded border border-blue-300"
                      />
                      <button
                        type="button"
                        onClick={() => removeImage(index)}
                        className="absolute -top-1 -right-1 bg-red-500 text-white rounded-full w-5 h-5 flex items-center justify-center text-xs font-bold border border-white"
                      >
                        ×
                      </button>
                    </div>
                  ))}
                </div>
              </div>
            </div>

            <button
              type="submit"
              className="w-full bg-blue-600 text-white font-medium py-3 rounded hover:bg-blue-700 transition-colors border border-blue-600"
            >
              THÊM NICK MỚI
            </button>
          </form>

          {/* CATEGORIES MANAGEMENT */}
          <div className="mt-6">
            <h3 className="text-lg font-bold text-blue-800 mb-4">
              Quản lý loại nick
            </h3>
            <div className="space-y-2">
              {categories.map((category, index) => (
                <div
                  key={category.id}
                  className="bg-blue-50 text-blue-800 px-4 py-3 rounded flex items-center justify-between border border-blue-200"
                >
                  <div className="flex items-center space-x-3">
                    <span className="text-sm font-bold bg-blue-600 text-white w-6 h-6 rounded flex items-center justify-center">
                      {index + 1}
                    </span>
                    <span className="text-sm">{category.name}</span>
                  </div>

                  <div className="flex items-center space-x-2">
                    <button
                      type="button"
                      onClick={() => handleReorderCategory(category.id, "up")}
                      disabled={index === 0}
                      className={`bg-blue-600 text-white w-8 h-8 rounded flex items-center justify-center hover:bg-blue-700 transition-colors text-sm font-bold border border-blue-600 ${
                        index === 0 ? "opacity-50 cursor-not-allowed" : ""
                      }`}
                    >
                      ↑
                    </button>

                    <button
                      type="button"
                      onClick={() => handleReorderCategory(category.id, "down")}
                      disabled={index === categories.length - 1}
                      className={`bg-blue-600 text-white w-8 h-8 rounded flex items-center justify-center hover:bg-blue-700 transition-colors text-sm font-bold border border-blue-600 ${
                        index === categories.length - 1
                          ? "opacity-50 cursor-not-allowed"
                          : ""
                      }`}
                    >
                      ↓
                    </button>

                    <button
                      type="button"
                      onClick={() => handleRemoveCategory(category.name)}
                      className="bg-red-600 text-white w-8 h-8 rounded flex items-center justify-center hover:bg-red-700 transition-colors text-sm font-bold border border-red-600"
                    >
                      ×
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
