import React, { useState, useEffect } from "react";
import AdminPanel from "../components/AdminPanel";
import UserManagement from "../components/UserManagement";
import LoginModal from "../components/LoginModal";

const Admin = ({
  addAccount,
  categories,
  addCategory,
  removeCategory,
  reorderCategory,
  addUser,
  deleteUser,
  users,
  fetchUsers,
  onLogin,
  onLogout,
  isLoggedIn,
  currentUser,
  deleteAccount,
}) => {
  const [currentView, setCurrentView] = useState("nicks");
  const [showLoginModal, setShowLoginModal] = useState(!isLoggedIn);

  useEffect(() => {
    setShowLoginModal(!isLoggedIn);
  }, [isLoggedIn]);

  const handleLoginSubmit = async (username, password) => {
    const result = await onLogin(username, password);
    if (result && result.success) {
      setShowLoginModal(false);
    }
    return result;
  };

  const handleLogoutClick = () => {
    onLogout();
    setCurrentView("nicks");
  };

  const handleCloseLoginModal = () => {
    setShowLoginModal(false);
  };

  if (!isLoggedIn) {
    return (
      <LoginModal
        isOpen={showLoginModal}
        onClose={handleCloseLoginModal}
        onLogin={handleLoginSubmit}
      />
    );
  }

  return (
    <div className="min-h-screen bg-white py-8">
      <div className="max-w-4xl mx-auto">
        {/* HEADER SWITCHER */}
        <div className="mb-6 text-center">
          <div className="bg-blue-50 rounded-lg p-4 border border-blue-200 inline-block">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => setCurrentView("nicks")}
                className={`font-medium px-6 py-3 rounded-lg transition-all duration-300 border ${
                  currentView === "nicks"
                    ? "bg-blue-600 text-white border-blue-600"
                    : "bg-white text-blue-800 border-blue-300 hover:bg-blue-50"
                }`}
              >
                Quản Lý Nick Game
              </button>

              {currentUser?.role === "admin" && (
                <button
                  onClick={() => setCurrentView("users")}
                  className={`font-medium px-6 py-3 rounded-lg transition-all duration-300 border ${
                    currentView === "users"
                      ? "bg-blue-600 text-white border-blue-600"
                      : "bg-white text-blue-800 border-blue-300 hover:bg-blue-50"
                  }`}
                >
                  Quản Lý Tài Khoản
                </button>
              )}

              <button
                onClick={handleLogoutClick}
                className="font-medium px-4 py-3 rounded-lg bg-red-600 text-white hover:bg-red-700 transition-all duration-300 border border-red-600"
              >
                Đăng xuất
              </button>
            </div>

            {/* USER INFO */}
            <div className="mt-3 text-blue-700">
              <p>
                Xin chào,{" "}
                <span className="font-bold">{currentUser?.username}</span> (
                {currentUser?.role === "admin" ? "Quản trị viên" : "Thành viên"}
                )
              </p>
            </div>
          </div>
        </div>

        {/* CONTENT */}
        {currentView === "nicks" ? (
          <AdminPanel
            addAccount={addAccount}
            categories={categories}
            addCategory={addCategory}
            removeCategory={removeCategory}
            reorderCategory={reorderCategory}
            isLoggedIn={isLoggedIn}
            currentUser={currentUser}
          />
        ) : (
          <UserManagement
            users={users}
            onAddUser={addUser}
            onDeleteUser={deleteUser}
            currentUser={currentUser}
            onBackToNicks={() => setCurrentView("nicks")}
            deleteAccount={deleteAccount}
          />
        )}
      </div>
    </div>
  );
};

export default Admin;
