import React, { useState, useEffect } from "react";
import AdminPanel from "../components/AdminPanel";
import UserManagement from "../components/UserManagement";
import LoginModal from "../components/LoginModal";

const Admin = ({
  addAccount,
  categories,
  addCategory,
  removeCategory,
  reorderCategory,
  addUser,
  deleteUser,
  users,
  fetchUsers,
  onLogin,
  onLogout,
  isLoggedIn,
  currentUser,
  deleteAccount,
}) => {
  const [currentView, setCurrentView] = useState("nicks");
  const [showLoginModal, setShowLoginModal] = useState(!isLoggedIn);

  useEffect(() => {
    setShowLoginModal(!isLoggedIn);
  }, [isLoggedIn]);

  const handleLoginSubmit = async (username, password) => {
    const result = await onLogin(username, password);
    if (result && result.success) {
      setShowLoginModal(false);
    }
    return result;
  };

  const handleLogoutClick = () => {
    onLogout();
    setCurrentView("nicks");
  };

  const handleCloseLoginModal = () => {
    setShowLoginModal(false);
  };

  if (!isLoggedIn) {
    return (
      <LoginModal
        isOpen={showLoginModal}
        onClose={handleCloseLoginModal}
        onLogin={handleLoginSubmit}
      />
    );
  }

  return (
    <div className="min-h-screen py-8">
      <div className="max-w-6xl mx-auto">
        {/* HEADER SWITCHER */}
        <div className="mb-6 text-center">
          <div className="bg-gradient-to-r from-cyan-500 via-blue-600 to-purple-600 rounded-2xl p-2 shadow-2xl inline-block">
            <div className="bg-gradient-to-br from-white via-blue-50 to-cyan-50 rounded-xl p-4 border-2 border-cyan-200 backdrop-blur-sm">
              <div className="flex items-center space-x-4">
                <button
                  onClick={() => setCurrentView("nicks")}
                  className={`font-bold px-6 py-3 rounded-xl transition-all duration-300 text-lg border-2 transform hover:scale-105 ${
                    currentView === "nicks"
                      ? "bg-gradient-to-r from-cyan-500 to-blue-600 text-white shadow-2xl border-cyan-400 scale-105"
                      : "bg-white text-blue-800 border-blue-300 hover:bg-blue-50 hover:border-blue-400"
                  }`}
                >
                  ‚ö° Qu·∫£n L√Ω Nick Game
                </button>

                {currentUser?.role === "admin" && (
                  <button
                    onClick={() => setCurrentView("users")}
                    className={`font-bold px-6 py-3 rounded-xl transition-all duration-300 text-lg border-2 transform hover:scale-105 ${
                      currentView === "users"
                        ? "bg-gradient-to-r from-cyan-500 to-blue-600 text-white shadow-2xl border-cyan-400 scale-105"
                        : "bg-white text-blue-800 border-blue-300 hover:bg-blue-50 hover:border-blue-400"
                    }`}
                  >
                    üë• Qu·∫£n L√Ω T√†i Kho·∫£n
                  </button>
                )}

                <button
                  onClick={handleLogoutClick}
                  className="font-bold px-4 py-3 rounded-xl bg-gradient-to-r from-red-500 to-pink-600 text-white hover:from-red-600 hover:to-pink-700 transition-all duration-300 border-2 border-red-400 transform hover:scale-105 shadow-lg"
                >
                  ƒêƒÉng xu·∫•t
                </button>
              </div>

              {/* USER INFO */}
              <div className="mt-3 text-blue-700">
                <p>
                  Xin ch√†o,{" "}
                  <span className="font-bold">{currentUser?.username}</span> (
                  {currentUser?.role === "admin"
                    ? "Qu·∫£n tr·ªã vi√™n"
                    : "Th√†nh vi√™n"}
                  )
                </p>
              </div>
            </div>
          </div>
        </div>

        {/* CONTENT */}
        {currentView === "nicks" ? (
          <AdminPanel
            addAccount={addAccount}
            categories={categories}
            addCategory={addCategory}
            removeCategory={removeCategory}
            reorderCategory={reorderCategory}
            isLoggedIn={isLoggedIn}
            currentUser={currentUser}
          />
        ) : (
          <UserManagement
            users={users}
            onAddUser={addUser}
            onDeleteUser={deleteUser}
            currentUser={currentUser}
            onBackToNicks={() => setCurrentView("nicks")}
            deleteAccount={deleteAccount}
          />
        )}
      </div>
    </div>
  );
};

export default Admin;
