import React, { useState, useEffect } from "react";
import CategorySection from "../components/CategorySection";
import GameDetailModal from "../components/GameDetailModal";
import VisitorCounter from "../components/VisitorCounter";

const Home = ({
  accounts,
  categories,
  deleteAccount,
  isLoggedIn,
  currentUser,
  pageViews,
  onIncrementPageViews,
}) => {
  const [selectedAccount, setSelectedAccount] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [titleAnimation, setTitleAnimation] = useState(false);

  useEffect(() => {
    onIncrementPageViews();

    // Kích hoạt animation cho title sau 100ms
    setTimeout(() => {
      setTitleAnimation(true);
    }, 100);
  }, []);

  const handleViewDetail = (account) => {
    setSelectedAccount(account);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setSelectedAccount(null);
  };

  return (
    <div className="min-h-screen">
      <div className="container mx-auto px-4 py-8">
        {/* HERO SECTION - THIẾT KẾ MỚI */}
        <div className="mb-12">
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
            {/* TÊN WEBSITE - CHIẾM 2 CỘT */}
            <div className="lg:col-span-2">
              <div className="bg-gradient-to-r from-blue-700 via-purple-700 to-cyan-600 rounded-2xl p-6 shadow-2xl border-2 border-cyan-400 relative overflow-hidden">
                {/* ANIMATED BACKGROUND */}
                <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent opacity-0 group-hover:opacity-10 transition-opacity duration-1000"></div>

                {/* TITLE WITH ANIMATION */}
                <div className="text-center">
                  <h1
                    className={`text-4xl md:text-5xl font-bold bg-gradient-to-r from-cyan-300 via-white to-amber-200 bg-clip-text text-transparent mb-4 transform transition-all duration-1000 ${
                      titleAnimation
                        ? "translate-y-0 opacity-100"
                        : "translate-y-10 opacity-0"
                    }`}
                  >
                    CHỢ ĐEN PUBG PC
                  </h1>
                  <div
                    className={`bg-white bg-opacity-15 rounded-xl p-4 border border-cyan-300 border-opacity-50 backdrop-blur-sm transform transition-all duration-1000 delay-300 ${
                      titleAnimation
                        ? "translate-y-0 opacity-100"
                        : "translate-y-6 opacity-0"
                    }`}
                  >
                    <p className="text-white text-lg font-semibold">
                      MUA BÁN TRAO ĐỔI TÀI KHOẢN PUBG PC
                    </p>
                  </div>
                </div>
              </div>
            </div>

            {/* THÔNG TIN LIÊN HỆ - CHIẾM 1 CỘT */}
            <div className="lg:col-span-1">
              <div className="bg-gradient-to-br from-cyan-600 to-blue-700 rounded-2xl p-5 shadow-2xl border-2 border-cyan-400 h-full relative overflow-hidden group">
                {/* SHINE EFFECT */}
                <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white to-transparent opacity-0 group-hover:opacity-20 -skew-x-12 group-hover:translate-x-full transition-all duration-1000"></div>

                <div className="text-center h-full flex flex-col justify-center space-y-3 relative z-10">
                  <div className="bg-white bg-opacity-15 rounded-lg p-3 border border-cyan-300 border-opacity-50 backdrop-blur-sm">
                    <p className="text-cyan-100 text-sm font-semibold mb-2">
                      LIÊN HỆ NGAY
                    </p>
                    <p className="text-white text-xl font-bold tracking-wide">
                      0922.010011
                    </p>
                  </div>
                  <div className="bg-gradient-to-r from-emerald-500 to-green-600 rounded-lg p-3 border border-emerald-400">
                    <p className="text-white text-xs font-semibold">
                      HỖ TRỢ 24/7 - BẢO HÀNH UY TÍN
                    </p>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* VISITOR COUNTER - VỊ TRÍ MỚI */}
          <div className="flex justify-center mb-8">
            <VisitorCounter count={pageViews} />
          </div>
        </div>

        {/* CATEGORIES */}
        {categories.map((category) => (
          <CategorySection
            key={category.id}
            category={category.name}
            accounts={accounts}
            onViewDetail={handleViewDetail}
            onDelete={deleteAccount}
            isLoggedIn={isLoggedIn}
            currentUser={currentUser}
          />
        ))}

        {accounts.length === 0 && (
          <div className="text-center py-16">
            <div className="bg-gradient-to-r from-blue-600 to-purple-700 rounded-2xl p-8 max-w-md mx-auto border-2 border-cyan-400 shadow-xl">
              <div className="w-16 h-16 bg-white bg-opacity-20 rounded-full flex items-center justify-center mx-auto mb-4 border-2 border-cyan-400">
                <div className="w-8 h-8 bg-cyan-400 rounded-full animate-pulse"></div>
              </div>
              <h3 className="text-xl font-bold text-white mb-3">
                ĐANG CẬP NHẬT NICK
              </h3>
              <p className="text-cyan-100 text-sm">
                Chúng tôi đang bổ sung thêm nick game chất lượng!
              </p>
            </div>
          </div>
        )}

        <GameDetailModal
          account={selectedAccount}
          isOpen={isModalOpen}
          onClose={handleCloseModal}
        />
      </div>
    </div>
  );
};

export default Home;
