import React, { useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import LoginModal from "./LoginModal";
import logoImageaa from "../image/anhlogo.png";

const Header = ({ isLoggedIn, currentUser, onLogin, onLogout }) => {
  const location = useLocation();
  const navigate = useNavigate();
  const [showLoginModal, setShowLoginModal] = useState(false);

  const handleLogin = async (username, password) => {
    const result = await onLogin(username, password);
    if (result.success) {
      setShowLoginModal(false);
    }
    return result;
  };

  const handleLogout = () => {
    onLogout();
    navigate("/");
  };

  const navItems = [
    { path: "/", label: "Trang Chủ" },
    { path: "/news", label: "Tin Tức" },
    { path: "/admin", label: "Quản Lý Nick" },
  ];

  return (
    <>
      <header className="bg-white shadow-sm border-b border-blue-200">
        <div className="container mx-auto px-4 py-4">
          <div className="flex flex-col sm:flex-row justify-between items-center space-y-4 sm:space-y-0">
            {/* LOGO */}
            <Link to="/" className="flex items-center space-x-4">
              <img
                src={logoImageaa}
                alt="ChoDenPUBGPC Logo"
                className="h-16 sm:h-20 w-auto object-contain"
              />
              <div className="text-center sm:text-left">
                <h1 className="text-2xl sm:text-3xl font-bold text-blue-800">
                  ChoDenPUBGPC
                </h1>
                <p className="text-blue-600 text-sm font-medium">
                  UY TÍN - CHẤT LƯỢNG - BẢO HÀNH
                </p>
              </div>
            </Link>

            {/* NAVIGATION & USER INFO */}
            <div className="flex flex-col sm:flex-row items-center space-y-2 sm:space-y-0 sm:space-x-4">
              {/* USER INFO */}
              {isLoggedIn && currentUser && (
                <div className="bg-blue-100 text-blue-800 px-4 py-2 rounded-lg text-center">
                  <p className="font-bold">{currentUser.username}</p>
                  <p className="text-xs">
                    {currentUser.role === "admin"
                      ? "Quản trị viên"
                      : "Thành viên"}
                  </p>
                </div>
              )}

              {/* NAVIGATION */}
              <nav className="flex items-center space-x-2 sm:space-x-4">
                {navItems.map((item) => (
                  <Link
                    key={item.path}
                    to={item.path}
                    className={`font-medium px-4 py-2 rounded-lg transition-all duration-300 text-base border ${
                      location.pathname === item.path
                        ? "bg-blue-600 text-white border-blue-600"
                        : "bg-white text-blue-800 border-blue-300 hover:bg-blue-50"
                    }`}
                  >
                    {item.label}
                  </Link>
                ))}
              </nav>
            </div>
          </div>
        </div>
      </header>

      {/* LOGIN MODAL */}
      <LoginModal
        isOpen={showLoginModal}
        onClose={() => setShowLoginModal(false)}
        onLogin={handleLogin}
      />
    </>
  );
};

export default Header;
