import React, { useState } from "react";
import GameCard from "./GameCard";

const CategorySection = ({
  category,
  accounts,
  onViewDetail,
  onDelete,
  isLoggedIn,
  currentUser,
}) => {
  const [showAll, setShowAll] = useState(false);

  const categoryAccounts = accounts.filter(
    (account) => account.category === category
  );
  const displayedAccounts = showAll
    ? categoryAccounts
    : categoryAccounts.slice(0, 4);

  if (categoryAccounts.length === 0) return null;

  return (
    <section className="mb-8">
      <div className="flex justify-between items-center mb-4">
        <div className="bg-blue-600 rounded-lg px-4 py-2">
          <h2 className="text-lg font-bold text-white">
            {category}{" "}
            <span className="text-blue-200">({categoryAccounts.length})</span>
          </h2>
        </div>

        {categoryAccounts.length > 4 && (
          <button
            onClick={() => setShowAll(!showAll)}
            className="bg-blue-100 text-blue-800 font-medium px-4 py-2 rounded-lg hover:bg-blue-200 transition-colors border border-blue-300"
          >
            {showAll ? "Thu gọn" : `Xem tất cả (${categoryAccounts.length})`}
          </button>
        )}
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
        {displayedAccounts.map((account) => (
          <GameCard
            key={account.id}
            account={account}
            onViewDetail={onViewDetail}
            onDelete={onDelete}
            isLoggedIn={isLoggedIn}
            currentUser={currentUser}
          />
        ))}
      </div>
    </section>
  );
};

export default CategorySection;
